import  paper from 'paper';
import "./portfolio.scss";
import React, {useState, useEffect} from 'react'
import EducationService from '../services/EducationService';

function Portfolio() {
    const paperStyle={padding:"50px 20px",}
    const [educations, setEducations] = useState([])

    useEffect(() => {
        getEducations()
    }, [])

    const getEducations = () => {

        EducationService.getEducations().then((response) => {
            setEducations(response.data)
            console.log(response.data);
        });
    };

    return (
     <div className='Portfolio' id="portfolio">

     <div className="left">

      <h2>Education</h2>
    
    

     
            
       
            <paper elevation={3} style={paperStyle}>
                      {  educations.map(
                                education =>
                                <paper elevation={6} style={{margin:"18px", padding:"15px",textAlign:"left"}} key = {education.id}> 
                                
                                     {education.id }
                                     {education.nom_etablissement }
                                     {education.periode }   
                                    {education.specialité }

                                
                                </paper>
                        )
                    
                      }
                      </paper>
                

        </div>
        <div className="right">Experience
        
        </div>
        
        </div>
    )
}

export default Portfolio;
