import { Paper} from '@material-ui/core';
import "./portfolio.scss";
import React, {useState, useEffect} from 'react'
import EducationService from '../services/EducationService';

function Portfolio() {
    const paperStyle={padding:"50px 20px", width:500,margin:"20px auto"}
    const [educations, setEducations] = useState([])

    useEffect(() => {
        getEducations()
    }, [])

    const getEducations = () => {

        EducationService.getEducations().then((response) => {
            setEducations(response.data)
            console.log(response.data);
        });
    };

    return (
     <div className='Portfolio' id="portfolio">

     <div className="left">

      <h2>Education</h2>
    
               <Paper elevation={3} style={paperStyle}>
                      {  educations.map(
                                education =>
                                <Paper elevation={6} style={{margin:"20px", padding:"20px",textAlign:"left",}} key = {education.id}> 
                                
                                    
                                {education.periode } : {education.nom_etablissement } <br/>
                                     
                                    {education.specialité }  <br/>

                                
                                </Paper>
                        )
                    
                      }
                      </Paper>
                
                     
        </div>
        
        <div className="right">Experience
        
        </div>
        
        </div>
        
    );
    
}

export default Portfolio;
